var craftableItems = [];
for (item in items){
    if (items[item]['recipe']) craftableItems.push(item);
}
var craftableItems = craftableItems.sort();
var usedItems = [];

var date = new Date();
var shortDate = date.toJSON().slice(0,10);
var rng = new Math.seedrandom(shortDate);
var secretItem = craftableItems[Math.floor((rng() * craftableItems.length))];
console.log(`secretItem = ${secretItem}`);

var currentItem = null;

function initLocalStorage(){
    if (! localStorage['date'] || localStorage['date'] != shortDate){
        localStorage['date'] = shortDate;
        localStorage['propositions'] = "[]";
    }
}

function reloadPropositions(){
    for (proposition of JSON.parse(localStorage['propositions'])){
        currentItem = proposition;
        var result = craft();
        addProposition(currentItem,result);
        usedItems.push(currentItem);
        if (!(result.includes('yellow') || result.includes('red'))) finishGame();
    }
}

function initCurrentItem(){
    currentItem = null;
    document.getElementById("itemSelection").value = "";
}

function initItemOptions(){
    var options = document.getElementById('itemOptions');
    for (item of craftableItems) options.innerHTML += `<option value="${items[item]['name']}"> ${items[item]['name']} </option>`;
}

function selectItem(){
    var input = document.getElementById("itemSelection");
    var selectedItem = input.value;
    selectedItem = selectedItem.normalize("NFD").replace(/[\u0300-\u036f]/g, "").toLowerCase();
    
    console.log(`selectedItem = ${selectedItem}`);
    if (selectedItem == "") currentItem = null;
    else currentItem = selectedItem;

    updateCraftingTable();
    updateSelectionImg();
}

function updateSelectionImg(){
    var slot = document.getElementById('selectionImg');
    if (currentItem == null){
        slot.innerHTML = "";
        return;
    }
    else if (! craftableItems.includes(currentItem)){
        slot.innerHTML = "??";
        return;
    }
    else {
        slot.innerHTML = generateImg(currentItem);
    }
}

function getCraftingSlots(){
    var craftingTable = document.getElementById('craftingTable');
    return [...craftingTable.querySelectorAll('td')];
}

function initCraftingTable(){
    var slots = getCraftingSlots();
    for (var index = 0; index<9; index ++) slots[index].innerText = '';
}

function updateCraftingTable(){
    if (! craftableItems.includes(currentItem)) {initCraftingTable();return;}

    var slots = getCraftingSlots();
    for (var index = 0; index<9; index ++){
        var recipe = items[currentItem]['recipe'];
        slots[index].innerHTML = generateImg(simplifyStr(recipe[index]));
    }
}

function generateImg(item){
    if (item == null) return "";
    if (! items[item]) return item;
    var img = items[item]['img'];
    if (img['type'] == "bloc"){
        return `<img src="${img.src}" alt="*${item}*" height=30px width=30px></img>`;
    }
    else return `<img class="${img.type}" alt="*${item}*" style="background-position: ${img['position']}" src="./img/pixel.png"></img>`;
}

function addProposition(item,correction){
    var propositions = document.getElementById('propositions');
    var currentHTML = propositions.innerHTML;
    var recipe = items[item]['recipe'];
    var name = items[item]['name'];
    var recipeTable = '<table class=craftingTable>';
    for (var row=0; row<3; row++){
        recipeTable += '<tr>';
        for (var column=0; column<3; column++){
            recipeTable += `<td class="${correction[3*row+column]}">${generateImg(simplifyStr(recipe[3*row+column]))}</td>`;
        }
        recipeTable += '</tr>';
    }
    recipeTable += '</table>';

    var propositionColor = "red";
    if (correction.includes('green')){
        if (correction.includes('red')) propositionColor = 'orange';
        else if (correction.includes('yellow')) propositionColor = 'yellow';
        else propositionColor = 'green';
    }
    else if (correction.includes('yellow')) propositionColor = 'orangered';

    propositions.innerHTML = `<tr class="proposition ${propositionColor}"><td>${generateImg(item)} ${name}</td><td>${recipeTable}</td>` + currentHTML;
}

function shareResult(){
    var colorsSymbols = {'green':"🟩",'yellow':"🟨",'red':"🟥"}
    var shareMessage = "PLAY 💎 CRAFTDLE ⛏ !\n";
    var link = "https://noisettetreschouette.github.io/Craftdle/";
    shareMessage += link + "\n\n";
    var propositions = [...document.querySelectorAll(".proposition .craftingTable")].reverse();
    shareMessage += `J'ai trouvé l'objet du jour en ${propositions.length} coup(s)\n`;
    for (proposition of propositions){
        var recipe = [...proposition.querySelectorAll('td')];
        var colorQuantities = {'green':0,"yellow":0,"red":0};
        for (ingredient of recipe){
            switch (ingredient.className) {
                case 'green':
                    colorQuantities["green"] ++;
                    break;
                case 'yellow':
                    colorQuantities["yellow"] ++;
                    break;
                case 'red':
                    colorQuantities["red"] ++;
                    break;
            }
        }
        for (var color in colorQuantities){
            var symbol = colorsSymbols[color];
            for (var _ = 0; _<colorQuantities[color];_++) shareMessage += symbol;
        }
        shareMessage += "\n";
    }
    navigator.clipboard.writeText(shareMessage);
    document.getElementById('craftButton').innerText = "COPIED";
}

function finishGame(){
    currentItem = null;
    updateCraftingTable();
    document.getElementById('itemSelection').value = "BRAVO !!!";
    var craftButton = document.getElementById('craftButton');
    craftButton.innerText = "SHARE";
    craftButton.onclick = shareResult;
    document.getElementById('selectionImg').innerHTML = "<img src='./img/GG.png' alt='🍰'></img>";
}

function generateErrorMessage(){
    var errorMessage = document.getElementById("errorMessage");
    if (currentItem == null){
        errorMessage.innerText = "Sélectionnez un objet";
        console.log('no item selected');
        return -1;
    }
    if (! craftableItems.includes(currentItem)) {
        errorMessage.innerText = "Objet introuvable";
        console.log('unknown item'); 
        return -1;
    }
    if (usedItems.includes(currentItem)) {
        errorMessage.innerText = "Objet déjà essayé";
        console.log('item already used'); 
        return -1;
    }
    return 1;    
}

function simplifyStr(str){
    if (str == null) return null;
    return str.normalize("NFD").replace(/[\u0300-\u036f]/g, "").toLowerCase();
}

function craftButton(){
    if (generateErrorMessage() == -1) return;
    errorMessage.innerText = "";

    console.log(`currentItem = ${currentItem}`);

    result = craft();

    addProposition(currentItem,result);

    usedItems.push(currentItem);
    var propositions = JSON.parse(localStorage['propositions']);
    propositions.push(currentItem);
    localStorage['propositions'] = JSON.stringify(propositions);

    initCurrentItem();

    updateCraftingTable();
    updateSelectionImg();

    if (!(result.includes('yellow') || result.includes('red'))) finishGame();
}

function craft(){
    var currentRecipe = items[currentItem]['recipe'];
    var secretRecipe = items[secretItem]['recipe'];

    var result = [];
    var usedSlots = [false,false,false,false,false,false,false,false,false];
    for (var index = 0; index<9; index++){
        if (currentRecipe[index] == secretRecipe[index]){
            result.push('green');
            usedSlots[index] = true;
        }
        else result.push('red');
    }
    for (var i = 0; i<9; i++){
        for (var j=0;j<9;j++){
            if (currentRecipe[i] == secretRecipe[j] && usedSlots[j]==false && result[i]=="red"){
                result[i] = "yellow";
                usedSlots[j] = true;
            }
        }
    }
    console.log(result);

    return result;
}

function setExample(){
    var playerRecipe = items['bouclier']['recipe'];

    var exampleSolution = items['barriere en chene'];
    var secretRecipe = exampleSolution['recipe']

    var craftingSlots = [...document.getElementById('exampleCraftingTable').querySelectorAll('td')];
    var propositionSlots = [...document.getElementById('exampleProposition').querySelectorAll('td')];

    var usedSlots = [false,false,false,false,false,false,false,false,false];

    for (var slot = 0; slot <9; slot ++){
        var ingredient = playerRecipe[slot];
        var img = generateImg(simplifyStr(ingredient));
        craftingSlots[slot].innerHTML = img;

        if (ingredient == secretRecipe[slot]){
            propositionSlots[slot].className = "green";
            usedSlots[slot] = true;
        }
        else {
            propositionSlots[slot].className = "red";
        }
    }
    for (var i = 0; i<9; i++){
        for (var j=0;j<9;j++){
            if (playerRecipe[i] == secretRecipe[j] && usedSlots[j]==false && propositionSlots[i].className=="red"){
                propositionSlots[i].className = "yellow";
                usedSlots[j] = true;
            }
        }
        propositionSlots[i].innerHTML = generateImg(simplifyStr(playerRecipe[i]));
    }

    var HTMLexampleSolution = "<table class='craftingTable'>";
    for (var i = 0; i<3; i++){
        HTMLexampleSolution += "<tr>";
        for (var j=0;j<3;j++){
            HTMLexampleSolution += `<td>${generateImg(simplifyStr(secretRecipe[3*i+j]))}</td>`;
        }
        HTMLexampleSolution += "</tr>";
    }
    HTMLexampleSolution += "</table>";
    document.getElementById('exempleSolution').innerHTML = HTMLexampleSolution;
}

function switchRules(){
    var rules = document.getElementById('rules')
    var style = rules.style['display'];
    if (style == 'none'){
        rules.style['display'] = 'inline';
    }
    else {
        rules.style['display'] = 'none';
    }
}